class Solution(object):
    def shortestPath(self, grid, k):
        """
        :type grid: List[List[int]]
        :type k: int
        :rtype: int
        """

        def canVisit(x, y, can_eliminate, distance):
            _can_eliminate, _distance = visited[x][y]
            if can_eliminate <= _can_eliminate and distance >= _distance:
                return False
            else:
                visited[x][y] = (can_eliminate, distance)
                return True

        M = len(grid)
        N = len(grid[0])

        DIRECTIONS = ((-1, 0), (1, 0), (0, -1), (0, 1))

        # corner case
        if M == 1 and N == 1:
            if k >= grid[0][0]:
                return 0
            else:
                return -1

        from collections import deque
        bfs = deque()
        # set (x, y) in the visited matrix,
        # x for the x obstacles can eliminate, y for the distance so far, for all explored paths
        visited = [[(k + 1, float("inf")) for _ in range(N)] for _ in range(M)]
        bfs.append((0, 0, k, 0))
        visited[0][0] = (k, 0)

        ans = float("inf")
        while bfs:
            x, y, can_eliminate, distance = bfs.popleft()
            for delta_x, delta_y in DIRECTIONS:
                new_x = x + delta_x
                new_y = y + delta_y
                if 0 <= new_x < M and 0 <= new_y < N:
                    if grid[new_x][new_y] == 1 and can_eliminate > 0:
                        if canVisit(new_x, new_y, can_eliminate - 1, distance + 1):
                            bfs.append((new_x, new_y, can_eliminate - 1, distance + 1))
                            if new_x == M - 1 and new_y == N - 1:
                                ans = min(ans, distance + 1)
                    elif grid[new_x][new_y] == 0:
                        if canVisit(new_x, new_y, can_eliminate, distance + 1):
                            bfs.append((new_x, new_y, can_eliminate, distance + 1))
                            if new_x == M - 1 and new_y == N - 1:
                                ans = min(ans, distance + 1)
        return -1 if ans == float("inf") else ans


