class Solution(object):
    def maximumLength(self, s):
        """
        :type s: str
        :rtype: int
        """
        # pre-process
        L = len(s)

        from collections import defaultdict
        dicts = defaultdict(dict)

        count, idx, prev = 1, 1, s[0]
        while idx < L:
            ch = s[idx]
            if ch == prev:
                count += 1
            else:
                if count not in dicts[prev]:
                    dicts[prev][count] = 0
                dicts[prev][count] += 1
                count = 1
                prev = ch
            idx += 1
        if count not in dicts[prev]:
            dicts[prev][count] = 0
        dicts[prev][count] += 1
        print(dicts)

        # process
        ans = -1
        for ch in dicts:
            for n in sorted(dicts[ch], reverse=True):
                if ans > n:
                    break
                if dicts[ch][n] >= 3:
                    ans = max(ans, n)
                elif dicts[ch][n] == 2:
                    ans = max(ans, n - 1)
                elif dicts[ch][n] == 1:
                    if n - 1 in dicts[ch]:
                        ans = max(ans, n - 1)
                    else:
                        ans = max(ans, n - 2)
        if ans == 0:
            ans = -1
        return ans


