class Solution(object):
    def smallestPalindrome(self, s, k):
        """
        :type s: str
        :type k: int
        :rtype: str
        """
        # pre-process
        # corner case
        if len(s) == 1 and k == 1:
            return s

        from collections import defaultdict
        # step 1 - get palindromic prefix
        def getPalindromicPrefix(s):
            dicts = defaultdict(int)
            for ch in s:
                dicts[ch] += 1

            res = ""
            middle = ""
            for ch in sorted(dicts.keys()):
                if dicts[ch] % 2 == 0:
                    res += ch * (dicts[ch] // 2)
                else:
                    res += ch * (dicts[ch] // 2)
                    middle = ch
            return res, middle

        prefix, middle = getPalindromicPrefix(s)
        print(prefix)

        # step 2 - get the part
        # helper function
        from collections import defaultdict
        def getCombinations(dicts, l):
            from math import factorial
            res = factorial(l)
            for ch in dicts:
                res //= factorial(dicts[ch])
            return res

        def getPart(prefix, k):
            l = len(prefix) - 1
            while l >= 0:
                dicts = defaultdict(int)
                for ch in prefix[l:]:
                    dicts[ch] += 1
                if getCombinations(dicts, len(prefix) - l) >= k:
                    return prefix[l:], l
                l -= 1
            return "", 0

        part, l = getPart(prefix, k)
        keep = prefix[:l]
        if part == "":
            return ""
        print(keep, part)

        # step 3 - place and calculate
        dicts = defaultdict(int)
        for ch in part:
            dicts[ch] += 1

        L = len(part)
        self.embeded = ""
        def dfs(idx, arrangement, total, dicts):
            if L == idx:
                self.embeded = arrangement
                return

            for ch in sorted(dicts.keys()):
                dicts[ch] -= 1
                if dicts[ch] == 0:
                    del dicts[ch]
                combs = getCombinations(dicts, len(part) - 1 - idx)
                if total + combs >= k:
                    return dfs(idx + 1, arrangement + ch, total, dicts)
                total += combs
                dicts[ch] += 1

        dfs(0, "", 0, dicts)
        # print(self.embeded)

        # process
        ans = keep + self.embeded + middle + self.embeded[::-1] + keep[::-1]
        return ans


