class Solution(object):
    def findLength(self, nums1, nums2):
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :rtype: int
        """
        N = len(nums1) + 1
        M = len(nums2) + 1

        # dp[x][y] - maximum length of repeated subarray ended with nums[x-1] and nums[y-1]
        dp = [[0] * M for _ in range(N)]

        for x in range(N):
            for y in range(M):
                if x == 0 or y == 0:
                    dp[x][y] = 0
                    continue

                if nums1[x-1] == nums2[y-1]:
                    dp[x][y] = dp[x-1][y-1] + 1

        ans = 0
        for x in range(N):
            for y in range(M):
                ans = max(ans, dp[x][y])
        return ans


