class Solution(object):
    def threeSumMulti(self, arr, target):
        """
        :type arr: List[int]
        :type target: int
        :rtype: int
        """
        def getCombinationsForTuple(tuple, dicts):
            left = 0
            right = 1
            ans = 1
            while right < len(tuple):
                if tuple[right] != tuple[left]:
                    ans *= getCombinations(right - left, dicts[tuple[left]])
                    left = right
                right += 1
            ans *= getCombinations(right - left, dicts[tuple[left]])
            return ans

        def getCombinations(k, n):
            ans = 1
            for x in range(k):
                ans = ans * (n - x) // (x + 1)
            return ans

        N = len(arr)

        # count number of unique item occurrence
        from collections import defaultdict
        dicts = defaultdict(int)
        for item in arr:
            dicts[item] += 1

        # count all unique tuples
        arr = sorted(arr)
        tuples = set()
        for idx in range(N):
            left = idx + 1
            right = N - 1
            while left < right:
                if arr[idx] + arr[left] + arr[right] == target:
                    tuples.add((arr[idx], arr[left], arr[right]))
                    left += 1
                    right -= 1
                elif arr[idx] + arr[left] + arr[right] < target:
                    left += 1
                else:
                    right -= 1

        # count with multiplicity
        ans = 0
        for tuple in tuples:
            ans += getCombinationsForTuple(tuple, dicts)
            ans %= 10**9 + 7
        return ans


